// import logo from './logo.svg';
import './globalCSS.css';
import Board from "./Board.jsx";
import Square from "./Square.jsx";
import { useState, useEffect } from 'react';

const defaultSquares = () => (new Array(9).fill(null)); //? returns a new array initialized with null values
const lines = [
  [0, 1, 2], [3, 4, 5], [6, 7, 8],
  [0, 3, 6], [1, 4, 7], [2, 5, 8],
  [0, 4, 8], [2, 4, 6]
]; // ? Winning combinations containing array indices

let App = () => {
  const [squares, setValue] = useState(defaultSquares());
  const [winner, setWinner] = useState(null);
  const [isPlayer1, setIsPlayer1] = useState(true); // true for Player 1, false for Player 2 (computer)
  
  useEffect(() => {
    const isComputerTurn = squares.filter(square => square !== null).length % 2 === 1;
  
    // Function to check for winning lines
    const linesThatAre = (a, b, c) => {
      return lines.filter(squareIndexes => {
        const squareValues = squareIndexes.map(index => squares[index]);
        return JSON.stringify([a, b, c].sort()) === JSON.stringify(squareValues.sort());
      });
    };
  
    // Check for empty squares to allow the computer to make a move
    const emptyIndexes = squares.map((square, index) => square === null ? index : null).filter(val => val !== null);
  
    // Check if player or computer has won
    const playerWon = linesThatAre('x', 'x', 'x').length > 0;
    const computerWon = linesThatAre('o', 'o', 'o').length > 0;
  
    if (playerWon) {
      setWinner('x');
    } else if (computerWon) {
      setWinner('o');
    } else if (squares.every(square => square !== null)) {
      // If no winner and board is full, it's a draw
      setWinner('draw');
    }
  
    // Function to put the computer's move on the board
    const putComputerAt = (index) => {
      let newSquares = [...squares]; // Create a new array to avoid mutation
      newSquares[index] = 'o'; // Place computer's move
      setValue(newSquares); // Update state with the new array
    };
  
    if (isComputerTurn && !isPlayer1) {
      // Computer move logic
      const winningLines = linesThatAre('o', 'o', null);
      if (winningLines.length > 0) {
        const winIndex = winningLines[0].filter(index => squares[index] === null)[0];
        putComputerAt(winIndex); // Make the winning move
        return;
      }
  
      const linesToBlock = linesThatAre('x', 'x', null);
      if (linesToBlock.length > 0) {
        const blockIndex = linesToBlock[0].filter(index => squares[index] === null)[0];
        putComputerAt(blockIndex); // Block player's winning move
        return;
      }
  
      const linesToContinue = linesThatAre('o', null, null);
      if (linesToContinue.length > 0) {
        putComputerAt(linesToContinue[0].filter(index => squares[index] === null)[0]);
        return;
      }
  
      // If no winning or blocking move, pick a random empty square
      const randomIndex = emptyIndexes[Math.floor(Math.random() * emptyIndexes.length)];
      putComputerAt(randomIndex);
    }
  
  }, [squares, isPlayer1]); // Effect runs when squares or isPlayer1 state changes
  

  const handleSquareClick = index => {
    if (squares[index] === null && isPlayer1) { // Only allow click if square is empty
      let newSquares = [...squares];
      newSquares[index] = 'x';
      setValue(newSquares);
    }
  };

  const togglePlayer = () => {
    setIsPlayer1(!isPlayer1);
    if (isPlayer1) {
      setValue(defaultSquares()); // Only reset board when switching to the computer
    }
    setWinner(null); // Reset winner
  };
  return (
    <main>
      <div className="buttons">
        <button onClick={togglePlayer}>{isPlayer1 ? 'Switch to Player 2 (Computer)' : 'Switch to Player 1'}</button>
      </div>
      <Board>
        {squares.map((square, index) =>
          <Square
            key={index}
            x={square === 'x' ? 1 : 0}
            o={square === 'o' ? 1 : 0}
            onClick={() => handleSquareClick(index)} />
        )}
      </Board>
      {!!winner && winner === 'o' && (
        <div className="result red">YOU LOST!!!</div>
      )}
      {!!winner && winner === 'x' && (
        <div className="result green">YOU WONðŸ˜ŽðŸ˜Ž</div>
      )}
      {!!winner && winner === 'draw' && (
        <div className="result">It's a DRAW!</div>
      )}
    </main>
  );
}

export default App;
